import React, { useState } from "react";
import "./about.css";
import aboutMyself from "../src/images/about1.png";
import reactIcon from "../src/images/reactIcon.png";
import jsIcon from "../src/images/jsIcon.png";
import bootstrapIcon from "../src/images/bootstrapIcon.png";
import cssIcon from "../src/images/css3.png";
import htmlIcon from "../src/images/htmlIcon.png";
import text from "../src/images/text.png";
import Modal from "../src/Modal";

const About = () => {
  const [openModal, setOpenModal] = useState(false);
  let [styling, setStyling] = useState(false);
  styling = { filter: " blur(8px)" };
  return (
    <>
      <div className="container-fluid ps-0" onClick={setStyling(true)}>
        <div className="row bg-light">
          <div className="col-md-4 d-flex justify-content-center">
            <img className="img-fluid my-image" src={aboutMyself} alt="logo" />
          </div>
          <div className="col-md-8 px-4 d-flex flex-column justify-content-center">
            <h2 className="fw-bolder fs-1">About Myself</h2>
            <p className="fs-5 mt-3">
              I am a FrontEnd Developer working for around 1.5 year, building
              and maintaining responsive websites. Proficient in HTML, CSS,
              Bootstrap, Javascript plus mordern library like React.js. Looking
              for an opportunity to work on React.js.
            </p>
            <a
              class="btn btn-outline-primary border-2 rounded-pill mb-4 mt-2 resume-btn"
              href="https://drive.google.com/file/d/1dORey76xAyP7mZYzWoeYoyg-gqx1K_jd/view?usp=sharing"
              role="button"
              target="_blank"
              download="Resume"
              rel="noreferrer noopener"
            >
              <img className="img-fluid me-2 resume" src={text} alt="logo" />
              Resume
            </a>
          </div>
        </div>
        <div className="container">
          <h2 className="fw-bolder fs-2 mt-3">Skills</h2>
          <div className="row">
            <div className="col">
              <button
                className="btn btn-outline-light rounded-circle"
                onClick={() => {
                  setOpenModal(true);
                }}
              >
                <img className="img-fluid" src={reactIcon} alt="react icon" />
              </button>
            </div>
            <div className="col">
              <button
                className="btn btn-outline-light rounded-circle"
                onClick={() => {
                  setOpenModal(true);
                }}
              >
                <img className="img-fluid" src={jsIcon} alt="js icon" />
              </button>
            </div>
            <div className="col">
              <button
                className="btn btn-outline-light rounded-circle"
                onClick={() => {
                  setOpenModal(true);
                }}
              >
                <img
                  className="img-fluid"
                  src={bootstrapIcon}
                  alt="bootstrap icon"
                />
              </button>
            </div>
            <div className="col">
              <button
                className="btn btn-outline-light rounded-circle"
                onClick={() => {
                  setOpenModal(true);
                }}
              >
                <img className="img-fluid" src={cssIcon} alt="css icon" />
              </button>
            </div>
            <div className="col">
              <button
                className="btn btn-outline-light rounded-circle"
                onClick={() => {
                  setOpenModal(true);
                }}
              >
                <img className="img-fluid" src={htmlIcon} alt="html icon" />
              </button>
            </div>
          </div>
        </div>
        {openModal && <Modal closeModal={setOpenModal} />}
      </div>
    </>
  );
};

export default About;
