import React from "react";
import web from "../images/mypics/1.jpeg";

export default function BlogA() {
  return (
    <>
      <div className="container">
        <div
          className={`img-box content-box ${
            index % 2 === 0 ? "flex-row" : "flex-row-reverse"
          }`}
        >
          <div className="col-md-6">
            <img className="img-fluid img-box" src={web} alt="" />
          </div>
          <div className="col-md-6 content-box">
            <h3>Hi This is Satyam</h3>
            <p>I want to show some tutorials to explain about photography</p>
          </div>
        </div>
      </div>
    </>
  );
}
